// Identify which agent processed the request and prepare data for Auditor

// Get the current item data (agent output)
const currentData = $input.item.json;

// Get the switchboard decision to know which agent was selected
const switchboardDecision = $('Agente Switchboard').item.json.output;
const selectedAgent = switchboardDecision.destinos[0];

// Extract the actual output - handle both direct output and nested output property
let agentOutput = currentData.output || currentData;

// If the output is a string, try to parse it as JSON
if (typeof agentOutput === 'string') {
  try {
    agentOutput = JSON.parse(agentOutput);
  } catch (e) {
    // Keep as string if parsing fails
  }
}

// Ensure we have a valid output
if (!agentOutput || (typeof agentOutput === 'object' && Object.keys(agentOutput).length === 0)) {
  agentOutput = {
    "Respuesta": "Error: No se recibi√≥ respuesta del agente especializado.",
    "Normativa": []
  };
}

// Return the properly formatted data for the Auditor
return [{
  json: {
    output: agentOutput,
    agentName: selectedAgent,
    debugInfo: {
      rawInput: currentData,
      parsedOutput: agentOutput,
      selectedAgent: selectedAgent
    }
  }
}];

return [{
  json: {
    output: "Lo siento, tu consulta est√° fuera del alcance de los agentes disponibles (BCRA, Comex, Senasa). Por favor, realiza una pregunta sobre regulaciones argentinas de comercio exterior, normativa cambiaria o sanidad agro-alimentaria.",
    agentName: "FueraDeAlcance"
  }
}];

// Final Response Formatter
const auditorOutput = $input.item.json;

// Parse the output if it's a string
let parsedOutput;
try {
  parsedOutput = typeof auditorOutput.output === 'string' 
    ? JSON.parse(auditorOutput.output) 
    : auditorOutput.output;
} catch (error) {
  return [{
    json: {
      response: "‚ùå Error al procesar la respuesta. Por favor, intente nuevamente.",
      error: true
    }
  }];
}

// Extract the final response
const finalResponse = parsedOutput.respuesta_final;
const status = parsedOutput.status;

// Build the formatted response
let formattedResponse = '';

if (status === 'Aprobado' && finalResponse) {
  // Title
  formattedResponse += `${finalResponse.titulo}\n\n`;

  // Direct response
  formattedResponse += `${finalResponse.respuesta_directa}\n\n`;

  // Key details
  if (finalResponse.detalles && finalResponse.detalles.length > 0) {
    formattedResponse += `**Informaci√≥n Clave:**\n`;
    finalResponse.detalles.forEach(detail => {
      formattedResponse += `${detail}\n`;
    });
    formattedResponse += '\n';
  }

  // Applicable regulations
  if (finalResponse.normativa_aplicable && finalResponse.normativa_aplicable.length > 0) {
    formattedResponse += `**Normativa Aplicable:**\n`;
    finalResponse.normativa_aplicable.forEach(norma => {
      formattedResponse += `${norma}\n`;
    });
    formattedResponse += '\n';
  }

  // Next action
  if (finalResponse.proxima_accion) {
    formattedResponse += `**¬øQu√© hacer ahora?**\n`;
    formattedResponse += `${finalResponse.proxima_accion}\n\n`;
  }

  // Warnings
  if (finalResponse.advertencias) {
    formattedResponse += `${finalResponse.advertencias}\n\n`;
  }

  // Footer with metadata
  formattedResponse += `---\n`;
  formattedResponse += `*Consultado: ${parsedOutput.metadata?.agente_consultado || 'Sistema'}*\n`;
  formattedResponse += `*Confianza: ${Math.round((parsedOutput.metadata?.confianza || 0.95) * 100)}%*`;

} else if (status === 'Observado') {
  formattedResponse = `‚ö†Ô∏è **Respuesta con Observaciones**\n\n`;
  formattedResponse += `La consulta fue procesada pero requiere aclaraciones:\n`;
  formattedResponse += `${parsedOutput.motivo_auditoria}\n\n`;
  formattedResponse += `Por favor, reformule su consulta con m√°s detalles.`;

} else if (status === 'Rechazado') {
  formattedResponse = `‚ùå **No se pudo procesar la consulta**\n\n`;
  formattedResponse += `Motivo: ${parsedOutput.motivo_auditoria}\n\n`;
  formattedResponse += `Por favor, verifique su consulta e intente nuevamente.`;

} else {
  // Handle out of scope or insufficient context
  formattedResponse = parsedOutput.respuesta_final || 
    "Lo siento, no puedo procesar esta consulta. Por favor, realice una pregunta sobre regulaciones argentinas de BCRA, Comercio Exterior o Senasa.";
}

// Return the formatted response
return [{
  json: {
    response: formattedResponse,
    status: status,
    metadata: parsedOutput.metadata || {}
  }
}];

// Debug Collector - Captures full execution flow
const userQuery = $('When chat message received').item.json.chatInput;
const switchboardOutput = $('Agente Switchboard').item.json.output;
const agentName = $input.item.json.agentName || 'unknown';
const auditorInput = $('Auditor').item.json;
const finalResponse = $input.item.json;

// Try to get the specialized agent output
let specializedAgentOutput = null;
let specializedAgentRaw = null;
let debugInfo = null;

try {
  // Get the data from Add Agent Name node
  const addAgentData = $('Add Agent Name').item.json;
  specializedAgentRaw = addAgentData.output;
  debugInfo = addAgentData.debugInfo;

  // Try to parse if it's a string
  if (typeof specializedAgentRaw === 'string') {
    try {
      specializedAgentOutput = JSON.parse(specializedAgentRaw);
    } catch (e) {
      specializedAgentOutput = specializedAgentRaw;
    }
  } else {
    specializedAgentOutput = specializedAgentRaw;
  }
} catch (e) {
  specializedAgentOutput = 'Error capturing agent output: ' + e.message;
}

// Parse auditor output safely
let auditorOutput = null;
try {
  if (auditorInput.output) {
    if (typeof auditorInput.output === 'string') {
      try {
        auditorOutput = JSON.parse(auditorInput.output);
      } catch (e) {
        auditorOutput = auditorInput.output;
      }
    } else {
      auditorOutput = auditorInput.output;
    }
  }
} catch (e) {
  auditorOutput = 'Error parsing auditor output';
}

// Build debug report
const debugReport = {
  'DEBUG_REPORT': {
    '1_user_query': userQuery,
    '2_switchboard_decision': switchboardOutput,
    '3_routed_to_agent': agentName,
    '4_agent_raw_output': specializedAgentRaw,
    '5_agent_parsed_output': specializedAgentOutput,
    '5a_debug_info': debugInfo,
    '6_auditor_input': auditorInput,
    '7_auditor_output': auditorOutput,
    '8_final_formatted_response': finalResponse.response,
    '9_execution_metadata': {
      'timestamp': new Date().toISOString(),
      'workflow_instance': $execution.id,
      'status': finalResponse.status || 'unknown'
    }
  }
};

// Create a formatted debug string for easy sharing
let auditorString = 'null';
try {
  auditorString = JSON.stringify(auditorOutput, null, 2);
} catch (e) {
  auditorString = String(auditorOutput);
}

const debugString = `
========== BUREAUCRACY ORACLE DEBUG ==========
USER QUERY: ${userQuery}

SWITCHBOARD DECISION:
${JSON.stringify(switchboardOutput, null, 2)}

ROUTED TO: ${agentName}

AGENT RESPONSE:
${JSON.stringify(specializedAgentOutput, null, 2)}

AUDITOR VERDICT:
${auditorString}

FINAL USER RESPONSE:
${finalResponse.response}

EXECUTED: ${new Date().toISOString()}
===========================================
`;

return [{
  json: {
    response: finalResponse.response,
    debug: debugReport,
    debugString: debugString,
    shareableDebug: Buffer.from(JSON.stringify(debugReport)).toString('base64')
  }
}];
